e2e.openpgp.constants.NAME_TO_ID[
    e2e.openpgp.constants.Type.HASH][
        e2e.hash.Algorithm.MD5] = 1;
e2e.openpgp.constants.NAME_TO_ID[
    e2e.openpgp.constants.Type.HASH][
        e2e.hash.Algorithm.SHA1] = 2;
e2e.openpgp.constants.NAME_TO_ID[
    e2e.openpgp.constants.Type.HASH][
        e2e.hash.Algorithm.RIPEMD] = 3;
e2e.openpgp.constants.NAME_TO_ID[
    e2e.openpgp.constants.Type.HASH][
        e2e.hash.Algorithm.SHA256] = 8;
e2e.openpgp.constants.NAME_TO_ID[
    e2e.openpgp.constants.Type.HASH][
        e2e.hash.Algorithm.SHA384] = 9;
