    rPrev = r;
    r = l ^ this.f2_(r, this.key.masking[1], this.key.rotate[1]);
    l = rPrev;

    rPrev = r;
    r = l ^ this.f3_(r, this.key.masking[2], this.key.rotate[2]);
    l = rPrev;

    rPrev = r;
    r = l ^ this.f1_(r, this.key.masking[3], this.key.rotate[3]);
